// fromResource
export function fromResource(resource) {
    return {
        namespace: resource.namespace.name,
        resource: resource.name,
        restPath: getRestPath(resource)
    };
}
// getRestPath
export function getRestPath(resource) {
    if (resource.annotations && resource.annotations["OpenApiRestPath"]) {
        return resource.annotations["OpenApiRestPath"];
    }
    else if (!resource.namespace.name || resource.namespace.name === "default") {
        return slug(resource.name);
    }
    else {
        return slug(resource.namespace.name) + "-" + slug(resource.name);
    }
}
// slug
function slug(s) {
    var result = '';
    for (var i = 0; i < s.length; i++) {
        var char = s[i];
        if (i > 0 && char !== char.toLowerCase()) {
            result += '-';
        }
        result += char.toLowerCase();
    }
    return result;
}
